export interface PaginatedResponse<T> {
    items: T[];
    total: number;
    page: number;
    pageSize: number;
    totalPages: number;
}
export interface PaginationParams {
    page?: number;
    limit?: number;
}
export interface ApiResponse<T> {
    data?: T;
    error?: string;
    message: string;
    success: boolean;
}
